import React from 'react';
import Creatediv from './creatediv';


export default class extends React.Component{
    state={
        x:[0, 0, 0,
            0, 0, 0,
            0, 0, 0],
        turn:'X',
        won:'',
        cnt:0
    }
    clickhandler = (index) => {
        let newarr = this.state.x;
        console.log('INDEX: ', index);
        console.log(newarr);
        console.log('STATE', this.state);
        if(newarr[index] === 0 && !this.state.won){
            newarr[index] = this.state.turn;
            this.winchecker(newarr);
            console.log(newarr);
            let newturn;

            if(this.state.turn === 'X'){
                newturn = 'O';
            }else{
                newturn = 'X';
            }

            this.setState({x:newarr, turn:newturn, cnt:this.state.cnt + 1});
        }

    }
    winchecker = (arr) => {
        //0,1,2
        //3,4,5
        //6,7,8
        if(arr[0] + arr[1] + arr[2] === 'XXX' || arr[3] + arr[4] + arr[5] === 'XXX' || arr[6] + arr[7] + arr[8] === 'XXX'){
            console.log('X WON!!!');
            this.setState({...this.state, won:'X'});
        }
        if(arr[0] + arr[1] + arr[2] === 'OOO' || arr[3] + arr[4] + arr[5] === 'OOO' || arr[6] + arr[7] + arr[8] === 'OOO'){
            console.log('O WON!!!');
            this.setState({...this.state, won:'O'});
        }
        //0,3,6
        //1,4,7
        //2,5,8
        if(arr[0] + arr[3] + arr[6] === 'XXX' || arr[1] + arr[4] + arr[7] === 'XXX' || arr[2] + arr[5] + arr[8] === 'XXX'){
            console.log('X WON!!!');
            this.setState({...this.state, won:'X'});
        }
        if(arr[0] + arr[3] + arr[6] === 'OOO' || arr[1] + arr[4] + arr[7] === 'OOO' || arr[2] + arr[5] + arr[8] === 'OOO'){
            console.log('O WON!!!');
            this.setState({...this.state, won:'O'});
        }
        //0,4,8
        //2,4,6
        if(arr[0] + arr[4] + arr[8] === 'XXX' || arr[2] + arr[4] + arr[6] === 'XXX'){
            console.log('X WON!!!');
            this.setState({...this.state, won:'X'});
        }
        if(arr[0] + arr[4] + arr[8] === 'OOO' || arr[2] + arr[4] + arr[6] === 'OOO'){
            console.log('O WON!!!');
            this.setState({...this.state, won:'O'});
        }
    }


    render(){
        return(
            <>
                <div className='text'>{this.state.turn === 'X' && !this.state.won ? 'NOW TORN OF X': 'NOW TORN OF O'}</div>
                <div className='text'>{this.state.won ? this.state.won + ' WON!' : ''}</div>
                <div className='container'>
                    {this.state.x.map((element, index) => {
                    return <Creatediv key={index} element={element} index={index} turn={this.state.turn} click={this.clickhandler} />
                })}
                </div>
            </>
        );
    }


}
