import React from 'react';
import classes from '../comment-input/comment-input.module.css';
import { Contextt } from '../../app2';

export default class extends React.Component{
    state = {
        author:'',
        text:''
    }
    render(){
        return(
            <Contextt.Consumer>
                {
                    (obj) => {
                        return(
                            <div>
                                <input onChange={(e) => this.setState({...this.state, author:e.target.value}), console.log(this.state)}/>
                                <input onChange={(event) => this.setState({...this.state, text:e.target.value}),  console.log(e.target.value)}/>
                                <button>SEND</button>
                            </div>
                        );
                    }
                }
            </Contextt.Consumer>
        );

    }
}
