import React from 'react';
import classes from '../comment-input/comment-input.module.css';
import { Contextt } from '../../';

export default class extends React.Component{
    state = {
        author:'',
        text:''
    }
    delete = () => {
        this.setState({author:'', text:''});
    }

    render(){
        return(
            <Contextt.Consumer>
                {
                    (obj) => {
                        return(
                            <div className={classes.comment}>
                                <h2>Leave a comment</h2>
                                <input className={classes.nameinput} placeholder='Type your Name' value={this.state.author} onChange={(e) => {this.setState({...this.state, author:e.target.value})}}/>
                                <input className={classes.textinput} placeholder='Type your comment' value={this.state.text} onChange={(e) => {this.setState({...this.state, text:e.target.value})}}/>
                                <button onClick={(e) => {obj.newcomment(this.state.author, this.state.text); this.delete()}}>SEND</button>
                            </div>
                        );
                    }
                }
            </Contextt.Consumer>
        );

    }
}

