import React from 'react';

export default class extends React.Component{
    state = {
        todo:[
            {id:0, text:'Wash tthe car',done:false}
        ]
    }
    addtodo = (obj) => {
        let arr = [...this.state.todo, obj];
        this.setState({todo:arr});
    }
    deltodo = (id) => {
        let arr = [...this.state.todo];
        arr.splice(id, 1);
        this.setState({todo:arr});
    }
    done = (id) => {
        let arr = [...this.state.todo];
        arr[id].done = true;
        this.setState({todo:arr});
    }

    render(){
        return (
            <div className='todolist'>
                <input/>
                <button>ADD</button>
            </div>

        );
    }
}
