import React from 'react';
import classes from '../comment-input/comment-input.module.css';
import { Contextt } from '../../app2';

export default class extends React.Component{
    state = {
        author:'',
        text:''
    }
    render(){
        return(
            <Contextt.Consumer>
                {
                    (obj) => {
                        return(
                            <div className={classes.comment}>
                                <input className={classes.nameinput} placeholder='Type your name' value={this.state.author} onChange={(e) => {this.setState({...this.state, author:e.target.value})}}/>
                                <input classplaceholder='Type your comment' value={this.state.text} onChange={(e) => {this.setState({...this.state, text:e.target.value})}}/>
                                <button onClick={(e) => {obj.newcomment(this.state.author, this.state.text)}}>SEND</button>
                            </div>
                        );
                    }
                }
            </Contextt.Consumer>
        );

    }
}

