import React from 'react';
import {connect} from 'react-redux';


const List_component = (props) => {
    return(
        props.todos.map((v, i) => {
            return <div key={i} className={classes.div}>
                <input type='checkbox' value={v.done} onChange={() => {props.done(i)}}/>
                <p>{v.title}</p>
                <button onClick={() => {props.remove(i)}}>REMOVE</button>
            </div>
        })
    );
}

function MapStateToProps(state){
    return {
        todos:state.todos
    }
}

function MapDispatchToProps(dispatch){
    return {
        done:(id) => dispatch({type:'done', id}),
        remove:(id) => dispatch({type:'remove', id})
    }
}

export default connect(MapStateToProps, MapDispatchToProps)(List_component);
