import React from 'react';
import classes from './post.module.css';
import { Contextt } from '../../app2';
import {NavLink} from 'react-router-dom';

export default (props) => {
    return (
        <Contextt.Consumer>
            {
                (obj) => {
                    return (
                        <div>

                            <div 
                                className={classes.card} 
                                onClick={() => {obj.postClickHandler(props.id); 
                                console.log('PROPS.ID: ', props.id)
                                }}> 
                                <div className={classes.header}>
                                    <b> {props.author}</b>
                                    <span> {props.date}</span>
                                </div>
                                <div className={classes.comment}>
                                    <h2>{props.title}</h2>
                                    <p>{props.disc}</p>
                                </div>
                                <div className={classes.bottom_card}>
                                    <i className={classes.comment_ctn}>{props.comments.length} comments</i>
                                    <NavLink to={{
                                    pathname:'/posts',
                                    search:props.id,
                                    hash:'POST_INDX'
                                    }}>Read More</NavLink>
                                </div>
                            </div>
                            <Route path="/posts/:id" component={post}/>
                         </div>
                    );
                }
            }
        </Contextt.Consumer>
    );
}
