import React from 'react';
import Creatediv from './creatediv';


export default class extends React.Component{
    state={
        x:[0, 0, 0,
            0, 0, 0,
            0, 0, 0],
        turn:'X',
        won:''
    }
    clickhandler = (index) => {
        let newarr = this.state.x;
        console.log('INDEX: ', index);
        console.log(newarr);
        if(newarr[index] === 0){
            newarr[index] = this.state.turn;
            console.log(newarr);
            this.checkwin(newarr);
            let newturn;
            if(this.state.turn === 'X'){
                newturn = 'O';
            }else{
                newturn = 'X';
            }
            this.setState({x:newarr, turn:newturn});
        }

    }
    checkwin = (arr) => {
        if(arr[0] === 'X' && arr[1] === 'X' && arr[2]){
            console.log('X WON!!!');
            this.setState({...this.state, won:'X'});
        }
    }

    render(){
        return(
            <>
                {this.state.x.map((element, index) => {
                    return <Creatediv key={index} element={element} index={index} turn={this.state.turn} click={this.clickhandler} />
                })}
            </>
        );
    }


}
