import React from 'react';
import './App.css';
import {NavLink,Route, Switch, Router} from 'react-router-dom';

import Home from './components/home-component/home';
import About from './components/about-component/about';
import Login from './components/login-component/login';
import Contacts from './components/contact-page-component/contact-page';

import NavBarComponent from './components/navbar-component/navbar-component';
import NavBarLogged from './components/navbar-logged-component/navbar-logged';

const BASE_URL = 'https://contacts-telran.herokuapp.com';
export const Context = React.createContext();

export default class App extends React.Component{
  state = {
    loaderShow: false,
    contacts:[],
    token:''
  }

  componentDidMount(){
    console.log('APP MOUNTED');
    if(localStorage.getItem('token')){
      let new_contacts = this.getAllContacts(localStorage.getItem('token'));
      console.log('CONTACTS:', new_contacts);
      this.setState({...this.state, contacts:new_contacts, token:localStorage.getItem('token')});
    }
  }
  updateToken = (token) => {
    this.setState({...this.state, token:token});
  }

  getAllContacts = (token) => {
    return fetch(`${BASE_URL}/api/contact`,{
        method:'GET',
        headers:{
            Authorization:token
        }
    }).then(response => {
        if(response.ok){
            response.json().then((response) => {
                console.log('GETALLCONTACTS: ', response.contacts);
                return response.contacts;
            })
        }else{
            throw new Error(response.status);
        }
  });
}

  render(){
    return(
      <>
          <Context.Provider value={{ updatecontacts:this.updateContacts ,contacts:this.state.contacts, updatetoken:this.updateToken , deleteAllcontacts:this.deleteAllContacts, updateContact:this.updateContact, getAllContacts:this.getAllContacts, deleteById:this.deleteById, addContact:this.addContact, checkStorage:this.checkStorage}}>
            {this.state.token ? <NavBarLogged /> : <NavBarComponent/>}      
              <Switch>
                <Route path='/' exact component={Home} />
                <Route path='/about' component={About} />
                <Route path='/login' component={Login} />
                <Route path='/contacts' component={Contacts} />
              </Switch>
          </Context.Provider>
      </>
    );
  }
}
