import React from 'react';
import './App.css';
import {Route, Switch} from 'react-router-dom';

import Home from './components/home-component/home';
import About from './components/about-component/about';
import Login from './components/login-component/login';
import Contacts from './components/contact-page-component/contact-page';
import Add from './components/add-component/add';

import NavBarComponent from './components/navbar-component/navbar-component';
import NavBarLogged from './components/navbar-logged-component/navbar-logged';

export const Context = React.createContext();

export default class App extends React.Component{
  state = {
    loaderShow: false,
    token:''
  }

  componentDidMount(){
    if(localStorage.getItem('token')){
      this.setState({...this.state, token:localStorage.getItem('token')});
    }
  }

  componentDidUpdate(){
    console.log(this.state);
  }
  updateToken = (token) => {
    this.setState({loaderShow:false, token:token});
  }

  render(){
    console.log(this.state.token);
    return(
      <>
          <Context.Provider value={{token:this.state.token, updatecontacts:this.updateContacts ,contacts:this.state.contacts, updatetoken:this.updateToken , deleteAllcontacts:this.deleteAllContacts, updateContact:this.updateContact, getAllContacts:this.getAllContacts, deleteById:this.deleteById, addContact:this.addContact, checkStorage:this.checkStorage}}>
            {this.state.token.length !== 0 ? <NavBarLogged /> : <NavBarComponent/>}      
              <Switch>
                <Route path='/' exact component={Home} />
                <Route path='/about' component={About} />
                <Route path='/login' component={Login} />
                <Route path='/contacts' component={Contacts} />
                <Route path='/add' component={Add} />
              </Switch>
          </Context.Provider>
      </>
    );
  }
}
