import Creatediv from './creatediv';

export default class extends React.Component{
    state={
        x:[0, 0, 0,
            0, 0, 0,
            0, 0, 0],
        turn:'X'
    }
    clickhandler = (index) => {
        let newarr = this.state.x;
        console.log('INDEX: ', index);
        if(newarr[index] === 'X' || 'O'){

        }
        newarr[index] = this.state.turn;
        console.log(newarr);
        let newturn;
        if(this.state.turn === 'X'){
            newturn = 'O';
        }else{
            newturn = 'X';
        }
        this.setState({x:newarr, turn:newturn});
    }
    render(){
        return(
            this.state.x.map((element, index) => {
