import React from 'react';

export default class extends React.Component{
    state = {
        todo:[
            {id:0, text:'Wash tthe car',done:false}
        ]
    }
    addtodo = (obj) => {
        let arr = [...this.state.todo, obj];
        this.setState(arr)
    }
}
