class contact_item extends React.Component{
    constructor(props){
        super(props);
    }

    render(){
        return(
            <div className={classes.card} onClick={() => {props.history.push(`/contacts/${props.id}`)}}>
                <h2>{props.name} {props.lastname}</h2>
                <h3>{props.phone}</h3>
            </div>
        );
    }
}

export default withRouter(contact_item);
