const init = {
    todos:[
        {title:'todo', done:false}
    ],
    text: ''
}

export default function todoReducer(state = init, action){
    switch(action.type){
        case 'add': return {...state, todos:[...state.todos, {title: state.text, done:false}], text: ''};
        case 'done': {
            let arr = state.todos;
            arr[action.id].done = true;
            doneTodo.done = true;
            return {...state, }
        }
        case 'remove': return {...state, todos:[...state.todos.splice(action.id, 1)]}
        case 'updatetext' : return {...state, text:action.text}
        default: return state;
    }
}
