import React from 'react';
import classes from './post.module.css';
import {Contextt} from '.'
import {NavLink, Route} from 'react-router-dom';
import postPage from '../post-page/post-page';
import PostPageDetailed from '../POSTS_PAGE_DETAILED/post_page_detailed';

export default (props) => {
    return (
        <Contextt.Consumer>
            {
                (obj) => {
                    return (
                        <div>

                            <div 
                                className={classes.card} 
                                onClick={() => {obj.postClickHandler(props.id); 
                                console.log('PROPS.ID: ', props.id)
                                }}> 
                                <div className={classes.header}>
                                    <b> {props.author}</b>
                                    <span> {props.date}</span>
                                </div>
                                <div className={classes.comment}>
                                    <h2>{props.title}</h2>
                                    <p>{props.disc}</p>
                                </div>
                                <div className={classes.bottom_card}>
                                    <i className={classes.comment_ctn}>{props.comments.length} comments</i>
                                    <NavLink to={{pathname:`/posts/${props.id}`}}>Read More</NavLink>
                                    <Route path="/posts/:id"><PostPageDetailed /></Route>
                                </div>
                            </div>
                         </div>
                    );
                }
            }
        </Contextt.Consumer>
    );
}
