import React from 'react';
import Creatediv from './creatediv';


export default class extends React.Component{
    state={
        x:[0, 0, 0,
            0, 0, 0,
            0, 0, 0],
        turn:'X',
        won:'',
        cnt:0
    }
    clickhandler = (index) => {
        let newarr = this.state.x;
        console.log('INDEX: ', index);
        console.log(newarr);
        console.log('STATE', this.state);
        if(this.state.cnt >= 4){
            this.winchecker(newarr);
        }
        if(newarr[index] === 0 && !this.state.won){
            newarr[index] = this.state.turn;
            console.log(newarr);
            let newturn;

            if(this.state.turn === 'X'){
                newturn = 'O';
            }else{
                newturn = 'X';
            }

            this.setState({x:newarr, turn:newturn, cnt:this.state.cnt + 1});
        }

    }
    winchecker = (arr) => {
        //0,1,2
        //3,4,5
        //6,7,8

        //0,3,6
        //1,4,7
        //2,5,8

        //0,4,8
        //2,4,6

        let i = 0;
        let y = 0;
        let ifx = 0;
        let ify = 0;
        console.log('USING CHECKWINNER', arr);
        for(i = 0; i < 3; i++){ //  0,0,0, 1,1,1, 2,2,2,
            for(y = 0; y < 9; y+3){ //0,3,6, 0,3,6, 0,3,6
                console.log('FIRST:', i+y);
                if(arr[i+y] === 'X'){
                    if(ifx === 3){
                        console.log('X WON!!!');
                        this.setState({...this.state, won:'X'});
                        return;
                    }else{
                        ifx++;
                    }
                }
                if(arr[i+y] === 'O'){
                    if(ify === 3){
                        console.log('O WON!');
                        this.setState({...this.state, won:'O'});
                        return;
                    }
                }
            }
        }
        let z = 0;
        let t = 0;

        for(z = 0; z < 7; z+3){//  0,0,0,3,3,3,6,6,6
            for(t = 0; t<3; t++){//0,1,2,0,1,2,0,1,2
                console.log('SECOND: ', z+t);
                if(arr[z+t] === 'X'){
                    if(ifx === 3){
                        console.log('X WON!!!');
                        this.setState({...this.state, won:'X'});
                        return;
                    }else{
                        ifx++;
                    }
                }
                if(arr[z+t] === 'O'){
                    if(ify === 3){
                        console.log('O WON!');
                        this.setState({...this.state, won:'O'});
                        return;
                    }
                }
            }
        }
        this.setState({...this.state});
    }


    render(){
        return(
            <>
                <div className='text'>{this.state.turn === 'X' && !this.state.won ? 'NOW TORN OF X': 'NOW TORN OF O'}</div>
                <div className='text'>{this.state.won ? this.state.won + 'WON!' : ''}</div>
                <div className='container'>
                    {this.state.x.map((element, index) => {
                    return <Creatediv key={index} element={element} index={index} turn={this.state.turn} click={this.clickhandler} />
                })}
                </div>
            </>
        );
    }


}
