import React from 'react';
import {useEffect, useState} from 'react';
import classes from './SideBar.module.css';

import SearchField from '../SearchField//SearchField';
import ReportsList from '../ReportsList/ReportsList';

import Reload from '../../Icons/Reload.svg';
import Close from '../../Icons/Close.svg';

import Data from '../../Exercise/sidebar.json';

export default (props) => {
    const [state, updateState] = useState({reports:Data, searchReports:[], searchField:'', reverseSort:false});

    function onUpdateDataHandler(){
        updateState({...state, reports:Data, searchReports:[], searchField:'', reverseSort:false});
    }

    function onUpdateSearchField(request){
        let newData = state.reports.filter(report => report.name.toLowerCase().startsWith(request));
        updateState({...state, searchReports:newData, searchField:request});

    }

    function sortReports(arr, type){
        let temp = [...arr];
        switch(type){
            case 'ascending':
                temp.sort((a, b) => Number(a.updated) - Number(b.updated));     
            case 'descending':
                temp.sort((a, b) => Number(b.updated) - Number(a.updated));       
        }
        return temp;
    }

    useEffect(() => {
        let tmp = sortReports(state.reports);
        updateState({...state, reports:tmp});
    }, []);

    return (
        <div className={classes.sidebar}>
            <div className={classes.title}>
                <h2>Reports {state.reports.length}</h2>
                <div className={classes.rightside}>
                    <button className={classes.reload} onClick={onUpdateDataHandler}></button>
                    <button className={classes.close}  onClick={props.onCloseHandler}></button>
                </div>
            </div>

            {/* hook component returns search request */}
            <div>
                <SearchField className={classes.search} field={state.searchField} updateField={onUpdateSearchField}/>
            </div>


            {/* sorting and showing results */}
            {state.searchField ? <ReportsList list={state.searchReports}/> : <ReportsList list={state.reports}/>}

        </div>
    );
}
