import React from 'react';
import {connect} from 'react-redux';

const style ={
    border:'1px solid black',
    listStyle:'none',
    marginBottom:'20px'
}

const List_component = (props) => {
    return(
    props.todos.map((v, i) => 
    {return <li key={i} 
    className={style}>
         <input type='checkbox' checked={v.done ? 'checked' : ''} onChange={() => {props.done(i)}}/> <p>{v.title}</p> <button onClick={() => {props.remove(i)}}>REMOVE</button> </li>})
    );
}

function MapStateToProps(state){
    return {
        todos:state.todos
    }
}

function MapDispatchToProps(dispatch){
    return {
        done:(id) => dispatch({type:'DONE', id}),
        remove:(id) => dispatch({type:'REMOVE', id})
    }
}

export default connect(MapStateToProps, MapDispatchToProps)(List_component);
