import React from 'react';
import Todolist from './HW/todo';

export const Context = React.createContext();

export default class extends React.Component{
    state = {
        todo:[],
        search:''
    }
    addtodo = () => {
        let obj = {
            id:this.state.todo.length > 0 ? this.state.todo.length: 0,
            text:this.state.search,
            done:false
        };
        let arr = [...this.state.todo, obj];
        this.setState({...this.state, todo:arr, search:''});
        console.log(arr);
    }
    deltodo = (id) => {
        let arr = [...this.state.todo];
        arr.splice(id, 1);
        this.setState({...this.state, todo:arr});
        console.log(arr);
    }
    done = (id) => {
        console.log(id, this.state.todo);
        let arr = [...this.state.todo];
        if(arr[id].done){
            arr[id].done = false;
            this.setState({...this.state, todo:arr});
            console.log(arr);
        }else{
            arr[id].done = true;
            this.setState({...this.state, todo:arr});
            console.log(arr);
        }
    }
    inputvalue = (text) => {
        this.setState({todo:[...this.state.todo], search:text});
    }

    render(){
        return (
            <Context.Provider value={{arr:this.state.todo, deletefunction:this.deltodo, donefunction:this.done}}>
            {
                <div className='todolist'>
                    <h1>Todo React App</h1>
                    <hr/>
                    <div className='nav'>
                        <input placeholder='Type your todo' value={this.state.search} onChange={(e) => this.inputvalue(e.target.value)}/>
                        <button onClick={() => this.addtodo()}>ADD</button>
                    </div>
                    <hr/>
                    <Todolist/>
                </div>
            }
            </Context.Provider>
        );
    }
}
