import React, { useState } from 'react';

const API_URL = "pro.openweathermap.org/data/2.5/forecast/hourly?q=${}&appid=${}"; 

const API_KEY = "c63983b9fe739104ff3f1b17c44e11cc";


export default function (){
  const [state, updateState] = useState({searchField:"", loading:false, info:null});

  function onClickHandler(){
    fetch(`pro.openweathermap.org/data/2.5/forecast/hourly?q=${state.searchField}&appid=${API_KEY}`)
    .then((response) => {
      return response.json();
    })
    .then((data) => {
      console.log(data);
    });

    updateState({...state,searchField:"", loading:true});
    fetch(`pro.openweathermap.org/data/2.5/forecast/hourly?q=${state.searchField}&appid=${API_KEY}`)
    .then(response => {
      console.log(response.json());
      updateState({...state,loading:false, info:response})
    })
    .catch(err => {
      console.log(err);
    });
  };

  function onChangeHandler(e){
    console.log(e);
    updateState({...state, searchField:e.target.value})
  }

  return (
    <>
    <h1>Hello</h1>
    <input value={state.searchField} onChange={onChangeHandler}></input>
    <button onClick={onClickHandler}>CLICK</button>
    {state.loading ? <i>LOADING...</i> : null}
    {state.info ? <p>{state.info}</p> : null}
    </>
  );
}
