import React from 'react';

import {withRouter} from 'react-router-dom';

import tech from '../../img/technology.png';
import media from '../../img/multimedia.png';
import address from '../../img/buildings.png';

import classes from '../contact-item-detailed-component/contact-item-detailed.module.css';
import { Context } from '../../App';

const BASE_URL = 'https://contacts-telran.herokuapp.com';

class contact_item_detailed extends React.Component{
    constructor(props){
        super(props);
    }
    state= {
        contacts:this.props.contacts,
        editmode:false,
        fields:{
            address:'',
            description:'',
            email:'',
            lastName:'',
            name:'',
            phone:'',
        }
    }

    componentDidMount(){
        if(!localStorage.getItem('token')){
            this.props.history.push('./login');
        }
    }

    render(){  
        let contact = this.props.contacts[this.props.match.params.id];
        return (
            <Context.Consumer>
                {
                    obj => {
                        return(
                        <div className={classes.card}>
                            <h2>{contact.name} {contact.lastName}</h2>
                            <img src ={tech} alt='tech'/> {contact.phone} <br/>
                            <img src ={media} alt='media'/> {contact.email} <br />
                            <img src ={address} alt='buildings'/> {contact.address} <br />
                            <h3>Description: {contact.description}</h3>
                            <button>Edit</button><button onClick={() => {this.props.deleteById(obj.token, contact.id)}}>Remove</button>
                        </div>
                        );
                    }

                }
            </Context.Consumer>
        );
    }
}

export default withRouter(contact_item_detailed);


function form(contact){
    return(<div>
        <input value={this.state.fields.name} placeholder='Name' onChange={(e) => {this.setState({...this.state, fields:{...this.state.fields, name:e.target.value}})}}/>
        <input value={this.state.fields.lastName} placeholder='Last Name' onChange={(e) => {this.setState({...this.state, fields:{...this.state.fields, lastName:e.target.value}})}}/>
        <input value={this.state.fields.phone} placeholder='Phone' onChange={(e) => {this.setState({...this.state, fields:{...this.state.fields, phone:e.target.value}})}}/>
        <input value={this.state.fields.name} placeholder='Name' onChange={(e) => {this.setState({...this.state, fields:{...this.state.fields, name:e.target.value}})}}/>
    </div>)
}
