import React from 'react';
import {withRouter} from 'react-router-dom';
import { Context } from '../../App';
import Contactitem from '../contact-item-component/contact-item';

const BASE_URL = 'https://contacts-telran.herokuapp.com';

class ContactPage extends React.Component{
    constructor(props){
        super(props);
    }

    state = {
        contacts: []
    }

    componentDidMount(){
        if(!localStorage.getItem('token')){

            this.props.history.push('/login');
        }else{
            console.log('CONTACT PAGE GOT TOKEN');
            let new_contacts = this.getAllContacts(localStorage.getItem('token'));
            console.log(new_contacts);
            this.setState({contacts: new_contacts});
        }
    }

    getAllContacts = (token) => {
        return fetch(`${BASE_URL}/api/contact`,{
            method:'GET',
            headers:{
                Authorization:token
            }
        }).then(response => {
            if(response.ok){
                response.json().then((response) => {
                    console.log('GETALLCONTACTS: ', response.contacts);
                    return response.contacts;
                })
            }else{
                throw new Error(response.status);
            }
        });
    }

    render(){
        return(
            <div>
                {this.state.contacts.map((contact, index) => {
                    return (<Contactitem key={index} id={contact.id} name={contact.item} phone={contact.phone}/>);
                })};
            </div>
        );
    }
}

export default withRouter(ContactPage);
