import React from 'react';
import { Contextt } from '../../App';
import classes from '../post-page/post-page.module.css';
import Postinfo from '../';
import Postcomments from '../../components/postcomments/postcomments';
import Commentinput from '../comment-input/comment-input';

export default () => {
    return(
        <Contextt.Consumer>
            {
                (obj) => {
                    return(<>
                        <Postinfo  
                            id={obj.posts[obj.post].postId}
                            author={obj.posts[obj.post].author}
                            date={obj.posts[obj.post].date}
                            disc={obj.posts[obj.post].content}
                        />
                        <h2>Comments</h2>
                        {obj.posts[obj.post].comments.map((e, i) =>  <Postcomments key={i} author={e.author} text={e.text}/>)}
                        <Commentinput/>
                        </>
                    );
                }
            }
        </Contextt.Consumer>
    );
}
