import React from 'react';
import { Contextt } from '../../app2';
import classes from '../../components/post-page/post-page.module.css';


export default class extends React.Component{
    state = {
        post:null
    }

    updateState = (obj) => {
        this.setState({post:obj});
    }
    render(){

        return (
            <Contextt.Consumer>
                {
                    (obj) => {
                        this.updateState(obj.post);
                    }
                }
            </Contextt.Consumer>
        );
    }
}
