const init = {
    todos:[
        {title:'todo', done:false}, 
    ]
}

export default function todoReducer(state = init, action){
    switch(action.type){
        case 'add': return {...state, todos:[...state.todos, {title: action.title, done:false}]};
        case 'done': return {...state, todos:[...state.todos, action.newtodo]}
        case 'remove': return {...state, todos:[...state.todos.splice(action.id, 1)]}
    }
}
