import React from 'react';
import Creatediv from './creatediv';


export default class extends React.Component{
    state={
        x:[0, 0, 0,
            0, 0, 0,
            0, 0, 0],
        turn:'X',
        won:''
    }
    clickhandler = (index) => {
        let newarr = this.state.x;
        console.log('INDEX: ', index);
        console.log(newarr);

        if(newarr[index] === 0 && !this.state.won){
            newarr[index] = this.state.turn;
            console.log(newarr);
            let newturn;

            if(this.state.turn === 'X'){
                newturn = 'O';
            }else{
                newturn = 'X';
            }

            this.setState({x:newarr, turn:newturn});
        }

    }
    winchecker = (arr) => {
        //0,1,2
        //3,4,5
        //6,7,8

        //0,3,6
        //1,4,7
        //2,5,8

        //0,4,8
        //2,4,6

        let i = 0;
        let y = 0;
        let z = 0;
        let t = 0;

        let ifx = 0;
        let ify = 0;

        for(i = 0; i < 3; i++){ //  0,0,0, 1,1,1, 2,2,2,
            for(y = 0; y < 9; y+3){ //0,3,6, 0,3,6, 0,3,6
              if(arr[i+y] === 'X'){
                if(ifx === 3){
                    console.log('X WON!!!');
                    this.setState({...this.state, won:'X'});
                }else{
                    ifx++;
                }
              }
              if(arr[i+y] === 'O'){
                if(ify === 3){
                      this.setState({...this.state, won:'O'});
                  }
              }
            }
        }

        for(z = 0; z < 7; z+3){//0,0,0,3,3,3,6,6,6
            for(t = 0; t<3; t++){//0,1,2,0,1,2,0,1,2
                if(arr[i+y] === 'X'){
                    if(ifx === 3){
                        console.log('X WON!!!');
                        this.setState({...this.state, won:'X'});
                    }else{
                        ifx++;
                    }
                }
                if(arr[i+y] === 'O'){
                    if(ify === 3){
                        this.setState({...this.state, won:'O'});
                    }
                }
            }
        }
    }


    render(){
        return(
            <>
                <div className='text'>{this.state.turn === 'X' ? 'NOW TORN OF X': 'NOW TORN OF O'}</div>
                <div className='container'>
                    {this.state.x.map((element, index) => {
                    return <Creatediv key={index} element={element} index={index} turn={this.state.turn} click={this.clickhandler} />
                })}
                </div>
            </>
        );
    }


}
