import React from 'react';
import {connect} from 'react-redux';

const Input_component = (props) => {
    console.log(props);
    return(
        <div>
                <input value={props.text} placeholder='Type your todo...' name='text' onChange={(e) => {props.UPDATE(e.target.value)}}/>
                <button type='submit' onClick={() => {props.ADD()}}> ADD</button>
        </div>
    );
}

function MapStateToProps(state){
    return {
        text:state.text
    }
}

function MapDispatchToProps(dispatch){
    return {
        Updatetext: (text) => dispatch({type:'updatetext', text}),
        Add: () => dispatch({type:'ADD'})
    }
}


export default connect(MapStateToProps, MapDispatchToProps)(Input_component);
