import React from 'react';

import {connect} from 'react-redux';

const Input_component = (props) => {
    return(
        <div>
            <form action='#'  onSubmit={(e) => {e.preventDefault(); props.Updatetext(e.target.text.value)}}>
                <input value={props.text} placeholder='Type your todo...' name='text'/>
                <button type='submit'> ADD</button>
            </form>
        </div>
    );
}

function MapStateToProps(state){
    return {
        text:state.text
    }
}

function MapDispatchToProps(dispatch){
    return {
        Updatetext: (text) => dispatch({type:'updatetext', text})
    }
}


export default connect(MapStateToProps, MapDispatchToProps)(Input_component);
