import React from 'react';

import {withRouter, Route} from 'react-router-dom';
import { Context } from '../../App';

import ContactItemDetailed from '../contact-item-detailed-component/contact-item-detailed';
import Contactitem from '../contact-item-component/contact-item';
import classes from '../contact-page-component/contact-page.module.css';
import contactItemDetailed from '../contact-item-detailed-component/contact-item-detailed';

const BASE_URL = 'https://contacts-telran.herokuapp.com';

class ContactPage extends React.Component{
    constructor(props){
        super(props);
    }

    state = {
        contacts: [{name: 'No Contacts Here!'}]
    }

    componentDidMount(){
        if(!localStorage.getItem('token')){ 
            this.props.history.push('/login');
        }else{
            this.getAllContacts(localStorage.getItem('token'));
        }
    }
    componentDidUpdate(){
        if(localStorage.getItem('token')){ 
            this.getAllContacts(localStorage.getItem('token'));
        }
    }

    getAllContacts(token){
        return fetch(`${BASE_URL}/api/contact`,{
            method:'GET',
            headers:{
                Authorization:token
            }
        }).then(response => {
            if(response.ok){
                response.json().then((response) => {
                    console.log('GETALLCONTACTS: ', response.contacts);
                    this.setState({contacts:[...response.contacts]});
                })
            }else{
                throw new Error(response.status);
            }
        });
    }

    render(){
        if(this.state.contacts.length > 0){
            console.log('render: ', this.state.contacts);
            return(
                <div className={classes.contactspage}>
                    <div className={classes.leftdiv}>
                        {this.state.contacts.map((contact, index) => {
                            return (<Contactitem key={index} id={index} name={contact.name} lastname={contact.lastname} phone={contact.phone}/>);
                        })}
                    </div>
                    <div className={classes.rightdiv}>
                        <Route path='/contacts/:id' render={(props) => { return <ContactItemDetailed {...props} contacts={this.state.contacts}/>}}/>
                    </div>
                </div>
            );
        }else{
            return <div>fetching...</div>;
        }

    }
}

export default withRouter(ContactPage);
