import React from 'react';
import classes from '../comment-input/comment-input.module.css';

export default class extends React.Component{
    state
    render(){
        return(
            <div className={classes.comment}>
                <h2>Leave a comment</h2>
                <input className={classes.nameinput} placeholder='Type your name'/>
                <input className={classes.textinput} placeholder='Type your comment'/>
                <button>Send</button>
            </div>
        );

    }
}
