import React from 'react';
import { Contextt } from '../../App';
import classes from '../post-page/post-page.module.css';
import Postinfo from '../postinfo/postinfo';
import Postcomments from '../postcomments/postcomments';
import Commentinput from '../comment-input/comment-input';


export default (props) => {
    return(
        <Contextt.Consumer>
            {
                (obj) => {
                    let corrent_id = obj.returnid(props.match.params.id);
                    return(<>
                        <Postinfo  
                            id={obj.posts[corrent_id].postId}
                            author={obj.posts[corrent_id].author}
                            date={obj.posts[corrent_id].date}
                            disc={obj.posts[corrent_id].content}
                        />
                        <h2>Comments</h2>
                        {obj.posts[corrent_id].comments.map((e, i) =>  <Postcomments key={i} author={e.author} text={e.text}/>)}
                        <Commentinput id={props.match.params.id}/>
                        </>
                    );
                }
            }
        </Contextt.Consumer>
    );
}
