import React from 'react';
import './App.css';
import {NavLink,Route, Switch, Router} from 'react-router-dom';

import Home from './components/home-component/home';
import About from './components/about-component/about';
import Login from './components/login-component/login';
import Contacts from './components/contact-page-component/contact-page';

import NavBarComponent from './components/navbar-component/navbar-component';
import NavBarLogged from './components/navbar-logged-component/navbar-logged';

const BASE_URL = 'https://contacts-telran.herokuapp.com';
export const Context = React.createContext();

export default class App extends React.Component{
  state = {
    loaderShow: false,
    contacts:[],
    token:this.checkStorage
  }

  registration = (email, password) => {
    const auth = {email,password};
    const requestBody = JSON.stringify(auth);
    return fetch(`${BASE_URL}/api/registration`,{
        method:'POST',
        headers:{
            'Content-Type':'application/json; charset=utf-8'
        },
        body:requestBody
    }).then(response => {
        if(response.ok){
            console.log('STATUS: ', response.status);
            return response.json();
            //свапнуть роут на контакты
        }
        throw new Error(response.status);
    });
  }

  signIn = (email, password) => {
    const auth = {email,password};
    const request = JSON.stringify(auth);
    this.setState({...this.state, loaderShow:true});
    return fetch(`${BASE_URL}/api/login`, {
        method:'POST',
        headers:{'Content-Type':'application/json; charset=utf-8'},
        body:request
    }).then(response => {
        if(response.ok){
            console.log('SIGNIN STATUS:', response.status);
            response.json().then((response) => {
              console.log(response.token);
              localStorage.setItem("token", response.token);
              this.setState({...this.state, token:response.token, loaderShow:false});
            })
        }else{
          throw new Error(response.status);
        }

    });
  }

  deleteAllContacts = (token) => {
    return fetch(`${BASE_URL}/api/clear`, {
        method:'DELETE',
        headers:{
            Authorization:token
        }
    }).then(response => {
        if(response.ok){
            return true;
            //обновить список контактов
        }
        throw new Error(response.status);
    });
  }

  updateContact = (token, contact) => {
    let req = JSON.stringify(contact);
    return fetch(`${BASE_URL}/api/contact`, {
        method:"PUT",
        headers:{
            'Content-Type':'application/json; charset=utf-8',
            Authorization:token
        },
        body:req
    }).then(response => {
        if(response.ok){
            return response.status;
        }
        throw new Error(response.status);
    })
  }

  getAllContacts = (token) => {
    return fetch(`${BASE_URL}/api/contact`,{
        method:'GET',
        headers:{
            Authorization:token
        }
    }).then(response => {
        if(response.ok){
            return response.json();
        }
        throw new Error(response.status);
    });
  }

  deleteById = (token, id) => {
    return fetch(`${BASE_URL}/api/contact/${id}`,{
        method: 'DELETE',
        headers: {
            Authorization:token
        }
    }).then(response => {
        if(response.ok){
            return response.json();
        }
        throw new Error(response.status);
    });
  }
  addContact = (token, contact) => {
    let req = JSON.stringify(contact);
    console.log(req);
    return fetch(`${BASE_URL}/api/contact`, {
        method:"POST",
        headers:{
            'Content-Type':'application/json; charset=utf-8',
            Authorization:token
        },
        body:req
    }).then(response => {
        if(response.ok){
            return true;
        }
        throw new Error(response.status);
    })
  }

  checkStorage = () => {
    if(localStorage.getItem('token')){
      return(localStorage.getItem('token'));
      console.log(token);
    }
    return '';
  }

  render(){
    return(
      <>
          <Context.Provider value={{registration:this.registration, signin:this.signIn, deleteAllcontacts:this.deleteAllContacts, updateContact:this.updateContact, getAllContacts:this.getAllContacts, deleteById:this.deleteById, addContact:this.addContact, checkStorage:this.checkStorage}}>
            {this.state.token? <NavBarLogged /> : <NavBarComponent/>}      
              <Switch>
                <Route path='/' exact component={Home} />
                <Route path='/about' component={About} />
                <Route path='/login' component={Login} />
                <Route path='/contacts' component={Contacts} />
              </Switch>
          </Context.Provider>
      </>
    );
  }
}
