import React from 'react';
import { Contextt } from '../../app2';
import classes from '../../components/post-page'


export default () => {
    return(
        <>
            <Contextt.Consumer>
                {
                    (obj) => {
                        return (
                            <div className={classes.card} onClick={() => {obj.postClickHandler(props.id); console.log('PROPS.ID: ', props.id)}}> 
                            <div className={classes.header}>
                                <span> {props.author}</span>
                                <b> {props.date}</b>
                            </div>
                            <div className={classes.comment}>
                                <h2>{props.title}</h2>
                                <p>{props.disc}</p>
                            </div>
                            <i className={classes.comment_ctn}>{props.comments.length} views</i>
                        </div> 
                        );
                    }
                }
            </Contextt.Consumer>
        </>
    );
}
