import React from 'react';
import { Contextt } from '../../app2';
// import classes from '../../components/postinfo/postinfo.module.css';
import Postinfo from '../../components/postinfo/postinfo';
import Postcomments from '../../components/postcomments/postcomments';
import Commentinput from '../comment-input/comment-input';

export default () => {
    return(
        <Contextt.Consumer>
            {
                (obj) => {
                    console.log(obj);
                    return(<>
                        <button className={classes.back}  onClick={() => {obj.back()}}>BACK</button>
                        <Postinfo  
                            id={obj.post.postId}
                            author={obj.post.author}
                            date={obj.post.date}
                            title={obj.post.title}
                            disc={obj.post.content}
                        />
                        <h2>Comments</h2>
                        {obj.post.comments.map((e, i) =>  <Postcomments key={i} author={e.author} text={e.text}/>)}
                        <Commentinput/>
                        </>
                    );
                }
            }
        </Contextt.Consumer>
    );
}
