import React from 'react';
import classes from '../comment-input/comment-input.module.css';

export default class extends React.Component{
    state = {
        author:'',
        text:''
    }
    render(){
        return(
            <div className={classes.comment}>
                <h2>Leave a comment</h2>
                <input className={classes.nameinput} placeholder='Type your name' onChange={(e) => this.setState({...this.setState, author:e.target.value})}/>
                <input className={classes.textinput} placeholder='Type your comment'  onChange={(e) => this.setState({...this.setState, text:e.target.value})}/>
                <button onClick={() => newcomment(this.props.id, this.state)}>Send</button>
            </div>
        );

    }
}
