import React from 'react';
import {connect} from 'react-redux';

const style ={
    border:'1px solid black',
    listStyle:'none',
    marginBottom:'20px'
}

const List_component = (props) => {
    return(
        props.todos.map((v, i) => {
            return <li key={i}>
                <input value={v.done}/>
        <p>{v.title}</p>
            </li>
        })
    );
}

function MapStateToProps(state){
    return {
        todos:state.todos
    }
}

function MapDispatchToProps(dispatch){
    return {
        done:(id) => dispatch({type:'done', id}),
        remove:(id) => dispatch({type:'remove', id})
    }
}

export default connect(MapStateToProps, MapDispatchToProps)(List_component);
