import React from 'react';
import { Contextt } from '../../app2';
import classes from '../../components/postinfo/postinfo.module.css';
import Postinfo from '../../components/postinfo/postinfo';
import Postcomments from '../../'

export default () => {
    return(
        <Contextt.Consumer>
            {
                (obj) => {
                    return(<><Postinfo  
                                id={obj.postId}
                                author={obj.author}
                                date={obj.date}
                                title={obj.title}
                                disc={obj.shordDesc}
                            />
                            <Postcomments 
                                comments={obj.comments}/>
                            </>
                    );
                }
            }
        </Contextt.Consumer>
    );
}

