import React from 'react';
import Creatediv from './creatediv';

export default class extends React.Component{
    state={
        x:[[0, 0, 0],
            [0, 0, 0],
            [0, 0, 0]],
        turn:'X'
    }
    clickhandler = (x, y) => {
        let newarr = this.state.x;
        newarr[x][y] = this.state.turn;
        console.log(newarr);
        this.setState({...this.state, x:newarr});
    }

    render(){
        return(
            <>
                {this.state.turn === 'X' ? 'TURN OF X': 'TURN OF O'}

                {this.state.x.for((element, index) => {
                     return element.map((elementy, indexy) => {
                          return <Creatediv 
                            key={indexy}
                            x={index}
                            y={indexy}
                            inner={elementy}
                            click={this.clickhandler}/>})})}
            </>
        );
    }
}
